import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { PRODUCTS } from './productdress';

function Dress() {
  const [cartItems, setCartItems] = useState({});
  const history = useHistory();

  const addToCart = (id) => {
    setCartItems((prevCartItems) => ({
      ...prevCartItems,
      [id]: (prevCartItems[id] || 0) + 1,
    }));
  };

  const subFromCart = (id) => {
    setCartItems((prevCartItems) => {
      const newCartItems = { ...prevCartItems };
      if (newCartItems[id] > 1) {
        newCartItems[id] -= 1;
      } else {
        delete newCartItems[id];
      }
      return newCartItems;
    });
  };

  const removeFromCart = (id) => {
    setCartItems((prevCartItems) => {
      const newCartItems = { ...prevCartItems };
      delete newCartItems[id];
      return newCartItems;
    });
  };

  const totalAmount = () => {
    return Object.keys(cartItems).reduce((total, key) => {
      const product = PRODUCTS.find((productDress) => productDress.id === Number(key));
      return total + product.price * cartItems[key];
    }, 0);
  };

  const handleBuyNow = (id) => {
    addToCart(id);
    history.push('/buy');
  };

  return (
    <div className="text-grey-300 flex flex-wrap justify-center items-center gap-20 p-10 pr-96">
      {PRODUCTS.map((productDress) => (
        <div key={productDress.id}>
          <img className="w-80 h-80 object-contain" src={productDress.productImage} alt={productDress.productName} />
          <p>{productDress.productName}</p>
          <p>Birr {productDress.price}</p>
          <button onClick={() => addToCart(productDress.id)} className="border-2 drop-shadow-2xl p-2 rounded hover:bg-gray-300">
            Add to Cart
          </button>
          <br />
          <button onClick={() => handleBuyNow(productDress.id)} className="border-2 drop-shadow-2xl p-2 rounded hover:bg-gray-300">
            Buy
          </button>
        </div>
      ))}

      <div className="fixed p-4 right-0 top-0 bg-blue-100 h-screen w-80 overflow-y-scroll">
        <h1 className="text-white font-bold text-2xl">Your Cart</h1>
        <p className="text-3xl font-bold">Total: Birr {totalAmount()} </p>
        {Object.keys(cartItems).map((key) => {
          const productDress = PRODUCTS.find((product) => product.id === Number(key));
          if (productDress) {
            return (
              <div key={key} className="mb-4">
                <div className="flex justify-between items-center">
                  <div className="flex items-center">
                    <img className="w-20 h-20 my-4" src={productDress.productImage} alt={productDress.productName} />
                    <span className="text-2xl font-bold pl-2">X {cartItems[key]}</span>
                  </div>
                  <div className="flex flex-col gap-2 font-bold">
                    <button
                      onClick={() => removeFromCart(Number(key))}
                      className="text-black-500 bg-gray-200 hover:bg-blue-500 hover:text-white p-2 rounded"
                    >
                      Remove
                    </button>
                    <button
                      onClick={() => addToCart(Number(key))}
                      className="text-black-500 text-2xl hover:text-green-800"
                    >
                      +
                    </button>
                    <button
                      onClick={() => subFromCart(Number(key))}
                      className="text-black-500 text-2xl hover:text-green-800"
                    >
                      -
                    </button>
                  </div>
                </div>
                <div className="flex items-center space-x-4 italic">
                  <p>{productDress.productName}</p>
                  <p>-</p>
                  <p>Birr {productDress.price}</p>
                </div>
              </div>
            );
          }
          return null;
        })}
      </div>
    </div>
  );
}

export default Dress;
